
public class BitwiseDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		int b = 3;
		
		int c = a & b;
		
		System.out.println(c);
		
		//5 - 0101
		//3 - 0011
		//4&3 - 0
		
		int q = 4;
		System.out.println(q>>2);
		//4 - 0100 
		//0010
		//0001
	}

}
